import { Component } from '@angular/core';
import {Book} from "./dto/book";
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'front_end';
  bookList:Array<Book> =[];
  indexs:Number =0;
  constructor(private http:HttpClient) {
    http.get<Array<Book>>('http://localhost:8080/api/v1/books').
    subscribe(bookList=>this.bookList=bookList);
  }

  saveBook(isbn: HTMLInputElement, title: HTMLInputElement, btn: HTMLButtonElement) {
    if(btn.innerText==="Save"){
      let isbn1 =isbn.value;
      let title1=title.value;
      if(!isbn.value){
        isbn.select();
      }
      if(!title.value){
        title.select();
      }
      this.http.post('http://localhost:8080/api/v1/books',
        new Book(isbn.value,title.value)).subscribe((data)=>{
        this.bookList.push(new Book(isbn1,title1));
      });
    }
    else if(btn.innerText==="Edit"){
      this.http.patch<Book>('http://localhost:8080/api/v1/books',new Book(isbn.value,title.value)).
      subscribe(book=>{
        this.bookList.splice(+this.indexs,1,book);
      })
      btn.innerText="Save";
    }

    isbn.value="";
    title.value="";
    isbn.select();



  }

  clearText(isbn: HTMLInputElement, title: HTMLInputElement) {
    isbn.value="";
    title.value="";
    isbn.select();

  }

  deleteBook(book: Book) {
    this.http.delete(`http://localhost:8080/api/v1/books/${book.isbn}`).subscribe(data=>{
      var index = this.bookList.indexOf(book);
      this.bookList.splice(index,1);
    });
  }

  editBook(book: Book, isbn: HTMLInputElement, title: HTMLInputElement, btn: HTMLButtonElement) {
    isbn.value=book.isbn;
    title.value=book.title;
    this.indexs=this.bookList.indexOf(book);
    btn.innerText="Edit";


  }
}
<h1 class="text-center m-3">Book Management System</h1>
<div class="d-flex flex-column  px-3">
  <label class="text-center">ISBN</label>
  <input #isbn class="text-center" type="text" placeholder="Enter ISBN">
</div>
<div class="d-flex flex-column p-3">
  <label class="text-center">Title</label>
  <input #title class="text-center" type="text" placeholder="Enter Book Title">
</div>
<div class="d-flex justify-content-center gap-2">
  <button #btn (click)="saveBook(isbn,title,btn)" class="btn btn-primary">Save</button>
  <button (click)="clearText(isbn,title)" class="btn btn-warning">Clear</button>
</div>
<div id="search-container" class="d-flex flex-row-reverse ">
  <input id="search" type="text" placeholder="Search">
</div>


<table class="table table-bordered m-3">
  <thead>
  <tr>
    <th class="text-center">ISBN</th>
    <th class="text-center">Title</th>
    <th></th>
  </tr>
  </thead>
  <tbody *ngFor="let book of bookList">
  <tr>
    <td class="text-center">{{book.isbn}}</td>
    <td class="text-center">{{book.title}}</td>
    <td class="text-center">
      <span (click)="editBook(book,isbn,title,btn)"  id="edit" class="px-3"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pencil-square" viewBox="0 0 16 16">
  <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
  <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
</svg></span>
      <span (click)="deleteBook(book)" id="delete" ><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash3-fill" viewBox="0 0 16 16">
  <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5Zm-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5ZM4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5Z"/>
</svg></span>
    </td>
  </tr>
  </tbody>
</table>
